using System.Security.Cryptography;

namespace TaskManagementPortal
{
    public class Utility
    {
        public static string Decrypt(string base64Input)
        {
            string privateKey = "-----BEGIN RSA PRIVATE KEY----------END RSA PRIVATE KEY-----";
            byte[] bytesToDecrypt = Convert.FromBase64String(base64Input);
            using (RSA rsa = RSA.Create())
            {
                rsa.ImportFromPem(privateKey);
                byte[] decryptedBytes = rsa.Decrypt(bytesToDecrypt, RSAEncryptionPadding.Pkcs1);
                return Convert.ToBase64String(decryptedBytes);  // Return as Base64 string
            }
        }

        public static async Task<string> ConvertFileToBase64Async(IFormFile file)
        {
            if (file == null || file.Length == 0)
                throw new ArgumentException("File is null or empty.");

            using (var memoryStream = new MemoryStream())
            {
                await file.CopyToAsync(memoryStream);
                byte[] fileBytes = memoryStream.ToArray();
                return Convert.ToBase64String(fileBytes);
            }
        }
    }
}
